42.query:
fetch events
| filter in(event.category, array($category))
| filter in (labels.alerting_profile, array($alerting_profile))
| filter in (entity_tags, array($tags))
| filter event.kind == "DAVIS_PROBLEM" and event.status == "CLOSED"
| filter dt.davis.is_duplicate == false
|fieldsadd duree_min=round((todouble(resolved_problem_duration)/60000))/1000000
|fieldsadd duration = if(duree_min < 5, "00-05 min", else:
  if(duree_min < 10, "05-10 min", else:
  if(duree_min < 15, "10-15 min", else:
  if(duree_min < 20, "15-20 min", else:
  if(duree_min < 25, "20-25 min", else:
  if(duree_min < 30, "25-30 min", else:
  if(duree_min < 60, "30-60 min", else:  "> 60 min"
  )))))))
|summarize problems=countDistinct(display_id), by:{duration}
|fields problems, duration
|sort duration asc
